function countdown(e,t,o,n,r,u,c){let d=(new Date).getTime(),i=new Date(o,t-1,e,n,r,u).getTime()-d,l=Math.floor(i/1e3),m=Math.floor(l/60),a=Math.floor(m/60),T=Math.floor(a/24);a%=24,m%=60,l%=60,T=T<10?"0"+T:T,a=a<10?"0"+a:a,m=m<10?"0"+m:m,l=l<10?"0"+l:l,document.querySelector(c+"-day").innerText=T,document.querySelector(c+"-hour").innerText=a,document.querySelector(c+"-minute").innerText=m,document.querySelector(c+"-second").innerText=l,setTimeout(()=>countdown(e,t,o,n,r,u,c),1e3)}countdown(10,9,2020,10,10,10,".hero-counter");